import React, { useState } from 'react';
import axios from '../utils/axios';
import '../styles/Dashboard.css';

function Dashboard() {
    const [activeScenario, setActiveScenario] = useState(null);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [processLog, setProcessLog] = useState([]);

    // 로그 추가 함수
    const addLog = (step, status, message) => {
        const newLog = {
            id: Date.now(),
            timestamp: new Date().toLocaleTimeString(),
            step,
            status,
            message
        };
        setProcessLog(prev => [newLog, ...prev].slice(0, 10)); // 최근 10개만 유지
    };

    // 회원가입 처리
    const handleRegister = async (e) => {
        e.preventDefault();
        try {
            addLog('Filter', 'process', 'UsernamePasswordAuthenticationFilter 진입');
            addLog('검증', 'process', '@Valid 어노테이션으로 입력값 검증 중...');
            
            // 입력값 검증
            if (!username || !password) {
                addLog('검증', 'error', 'MethodArgumentNotValidException: 필수 필드 누락');
                return;
            }

            if (password.length < 8) {
                addLog('검증', 'error', 'ValidationException: 비밀번호는 8자 이상이어야 합니다.');
                return;
            }

            addLog('Filter', 'process', 'SecurityContextPersistenceFilter 처리 중');
            await new Promise(resolve => setTimeout(resolve, 500));

            addLog('암호화', 'process', 'BCryptPasswordEncoder.encode() 호출');
            await new Promise(resolve => setTimeout(resolve, 500));
            const mockEncodedPassword = `$2a$10$${password.repeat(2)}...`;
            addLog('암호화', 'success', `암호화 완료: ${mockEncodedPassword}`);

            addLog('DB', 'process', 'UserRepository.findByUsername() 호출 - 중복 검사');
            
            try {
                const response = await axios.post('/api/auth/register', {
                    username,
                    password
                });

                addLog('DB', 'success', 'UserRepository.save() 완료');
                addLog('Entity', 'success', `MemberEntity 생성됨 - ID: ${Math.floor(Math.random() * 1000)}`);
                addLog('Transaction', 'success', '@Transactional 커밋 완료');
                
                setUsername('');
                setPassword('');

            } catch (error) {
                if (error.response?.data?.includes('exists')) {
                    addLog('DB', 'error', 'DataIntegrityViolationException: 중복된 username');
                    addLog('Exception', 'error', 'UserAlreadyExistsException 발생');
                } else {
                    addLog('Exception', 'error', `${error.name}: ${error.message}`);
                }
            }
        } catch (error) {
            addLog('System', 'error', `시스템 오류: ${error.message}`);
        }
    };

    // 로그인 처리
    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            addLog('Filter', 'process', 'UsernamePasswordAuthenticationFilter 시작');
            addLog('Auth', 'process', 'AuthenticationManager.authenticate() 호출');

            addLog('Provider', 'process', 'DaoAuthenticationProvider 인증 시도');
            addLog('Service', 'process', 'UserDetailsService.loadUserByUsername() 호출');

            const response = await axios.post('/api/auth/login', {
                username,
                password
            });

            if (response.data.access_token) {
                const token = response.data.access_token;
                const tokenParts = token.split('.');
                
                addLog('JWT', 'success', '토큰 생성 완료');
                addLog('JWT', 'info', `Header: ${tokenParts[0]}`);
                addLog('JWT', 'info', `Payload: ${tokenParts[1]}`);
                addLog('JWT', 'info', `Signature: ${tokenParts[2].substring(0, 10)}...`);

                localStorage.setItem('token', token);
                addLog('Security', 'success', 'SecurityContextHolder에 인증 정보 저장');
                
                setUsername('');
                setPassword('');
            }

        } catch (error) {
            if (error.response?.status === 401) {
                addLog('Auth', 'error', 'BadCredentialsException: 잘못된 인증 정보');
                addLog('Security', 'process', 'SecurityContextHolder.clearContext() 호출');
            } else if (error.response?.status === 404) {
                addLog('Service', 'error', 'UsernameNotFoundException: 사용자를 찾을 수 없음');
            } else if (error.response?.status === 403) {
                addLog('Security', 'error', 'AccessDeniedException: 접근 권한 없음');
            } else {
                addLog('System', 'error', `AuthenticationException: ${error.message}`);
            }
            addLog('Filter', 'process', 'AuthenticationEntryPoint.commence() 호출됨');
        }
    };

    const securityScenarios = [
        {
            id: 'invalid-token',
            title: '토큰 인증 실패',
            cases: [
                { type: 'expired', desc: '만료된 토큰', handler: 'JwtAuthenticationFilter → TokenExpiredException' },
                { type: 'invalid', desc: '유효하지 않은 토큰', handler: 'JwtAuthenticationFilter → InvalidTokenException' },
                { type: 'missing', desc: '토큰 누락', handler: 'SecurityContext → AuthenticationException' }
            ]
        },
        {
            id: 'auth-fail',
            title: '인증 실패',
            cases: [
                { type: 'wrong-password', desc: '잘못된 비밀번호', handler: 'DaoAuthenticationProvider → BadCredentialsException' },
                { type: 'user-not-found', desc: '사용자 없음', handler: 'UserDetailsService → UsernameNotFoundException' },
                { type: 'locked', desc: '계정 잠금', handler: 'UserDetails → LockedException' }
            ]
        },
        {
            id: 'authorization',
            title: '권한 부족',
            cases: [
                { type: 'forbidden', desc: '접근 권한 없음', handler: 'AccessDecisionManager → AccessDeniedException' },
                { type: 'role-missing', desc: '필요 역할 없음', handler: '@PreAuthorize → InsufficientAuthenticationException' },
                { type: 'method-denied', desc: '메소드 접근 거부', handler: '@Secured → MethodSecurityException' }
            ]
        }
    ];

    return (
        <div className="dashboard-container">
            <div className="security-header">
                <h1><i className="fas fa-shield-alt"></i> Spring Security 예외 처리 대시보드</h1>
            </div>
            
            <div className="scenarios-grid">
                {securityScenarios.map(scenario => (
                    <div key={scenario.id} className="scenario-card">
                        <div className="scenario-header">
                            <h3>{scenario.title}</h3>
                        </div>
                        <div className="cases-list">
                            {scenario.cases.map(caseItem => (
                                <div key={caseItem.type} 
                                     className="case-item"
                                     onMouseEnter={() => setActiveScenario(caseItem.type)}
                                     onMouseLeave={() => setActiveScenario(null)}>
                                    <div className="case-icon">
                                        <i className={`fas ${
                                            caseItem.type.includes('token') ? 'fa-key' :
                                            caseItem.type.includes('password') ? 'fa-lock' :
                                            caseItem.type.includes('user') ? 'fa-user-slash' :
                                            'fa-ban'
                                        }`}></i>
                                    </div>
                                    <div className="case-content">
                                        <h4>{caseItem.desc}</h4>
                                        <div className="handler-chain">
                                            {caseItem.handler.split('→').map((step, idx) => (
                                                <span key={idx}>
                                                    {step.trim()}
                                                    {idx < caseItem.handler.split('→').length - 1 && 
                                                        <i className="fas fa-arrow-right"></i>}
                                                </span>
                                            ))}
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                ))}
            </div>

            <div className="security-stats">
                <div className="stat-item">
                    <i className="fas fa-shield-alt"></i>
                    <span>인증 필터</span>
                    <strong>활성</strong>
                </div>
                <div className="stat-item">
                    <i className="fas fa-lock"></i>
                    <span>JWT 검증</span>
                    <strong>정상</strong>
                </div>
                <div className="stat-item">
                    <i className="fas fa-user-shield"></i>
                    <span>권한 관리</span>
                    <strong>동작중</strong>
                </div>
            </div>

            <div className="realtime-section">
                <div className="auth-forms">
                    <div className="auth-form">
                        <h3>회원가입</h3>
                        <form onSubmit={handleRegister}>
                            <input
                                type="text"
                                placeholder="사용자명"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                            />
                            <input
                                type="password"
                                placeholder="비밀번호"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                            />
                            <button type="submit">회원가입</button>
                        </form>
                    </div>
                    <div className="auth-form">
                        <h3>로그인</h3>
                        <form onSubmit={handleLogin}>
                            <input
                                type="text"
                                placeholder="사용자명"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                            />
                            <input
                                type="password"
                                placeholder="비밀번호"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                            />
                            <button type="submit">로그인</button>
                        </form>
                    </div>
                </div>

                <div className="process-log">
                    <h3>처리 과정 로그</h3>
                    <div className="log-container">
                        {processLog.map(log => (
                            <div key={log.id} className={`log-item ${log.status}`}>
                                <span className="log-time">{log.timestamp}</span>
                                <span className="log-step">{log.step}</span>
                                <span className="log-message">{log.message}</span>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Dashboard;
