import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import axios from '../utils/axios';
import '../styles/Register.css';
import 'react-toastify/dist/ReactToastify.css';

function Register() {
    const [formData, setFormData] = useState({
        username: '',
        password: '',
        confirmPassword: '',
        email: ''
    });
    const navigate = useNavigate();

    const validateForm = () => {
        if (!formData.username || !formData.password || !formData.confirmPassword || !formData.email) {
            toast.error('모든 필드를 입력해주세요.');
            return false;
        }

        if (formData.username.length < 2 || formData.username.length > 30) {
            toast.error('사용자명은 2-30자 사이여야 합니다.');
            return false;
        }

        if (formData.password.length < 8) {
            toast.error('비밀번호는 8자 이상이어야 합니다.');
            return false;
        }

        if (!/[A-Za-z]/.test(formData.password) || !/[0-9]/.test(formData.password) || !/[!@#$%^&*]/.test(formData.password)) {
            toast.error('비밀번호는 영문, 숫자, 특수문자를 모두 포함해야 합니다.');
            return false;
        }

        if (formData.password !== formData.confirmPassword) {
            toast.error('비밀번호가 일치하지 않습니다.');
            return false;
        }

        if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
            toast.error('유효한 이메일 주소를 입력해주세요.');
            return false;
        }

        return true;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!validateForm()) return;

        try {
            toast.info('회원가입 처리 중...', {
                autoClose: 2000
            });

            const response = await axios.post('/api/auth/register', {
                username: formData.username,
                password: formData.password,
                email: formData.email
            });

            toast.success('회원가입이 완료되었습니다!', {
                onClose: () => navigate('/login')
            });

        } catch (error) {
            if (error.response) {
                switch (error.response.status) {
                    case 400:
                        if (error.response.data.includes('exists')) {
                            toast.error('이미 존재하는 사용자명입니다.');
                        } else if (error.response.data.includes('email')) {
                            toast.error('이미 사용 중인 이메일입니다.');
                        } else {
                            toast.error('입력값이 유효하지 않습니다.');
                        }
                        break;
                    case 500:
                        toast.error('서버 오류가 발생했습니다. 잠시 후 다시 시도해주세요.');
                        break;
                    default:
                        toast.error('회원가입 처리 중 오류가 발생했습니다.');
                }
            } else if (error.request) {
                toast.error('서버에 연결할 수 없습니다. 네트워크 연결을 확인해주세요.');
            } else {
                toast.error('알 수 없는 오류가 발생했습니다.');
            }
        }
    };

    return (
        <div className="register-page">
            <ToastContainer
                position="top-right"
                autoClose={3000}
                hideProgressBar={false}
                newestOnTop
                closeOnClick
                rtl={false}
                pauseOnFocusLoss
                draggable
                pauseOnHover
                theme="colored"
            />
            <div className="register-container">
                <div className="security-logo">
                    <div className="shield-icon">
                        <i className="fas fa-shield-alt"></i>
                    </div>
                    <h1>Spring Security</h1>
                    <p>안전한 회원가입</p>
                </div>

                <form onSubmit={handleSubmit} className="register-form">
                    <div className="form-group">
                        <label>
                            <i className="fas fa-user"></i>
                            사용자명
                        </label>
                        <input
                            type="text"
                            value={formData.username}
                            onChange={(e) => setFormData({...formData, username: e.target.value})}
                            placeholder="사용자명을 입력하세요"
                            required
                            minLength="2"
                            maxLength="30"
                        />
                        <small>2-30자 사이로 입력해주세요</small>
                    </div>

                    <div className="form-group">
                        <label>
                            <i className="fas fa-envelope"></i>
                            이메일
                        </label>
                        <input
                            type="email"
                            value={formData.email}
                            onChange={(e) => setFormData({...formData, email: e.target.value})}
                            placeholder="이메일을 입력하세요"
                            required
                        />
                    </div>

                    <div className="form-group">
                        <label>
                            <i className="fas fa-lock"></i>
                            비밀번호
                        </label>
                        <input
                            type="password"
                            value={formData.password}
                            onChange={(e) => setFormData({...formData, password: e.target.value})}
                            placeholder="비밀번호를 입력하세요"
                            required
                        />
                        <small>영문, 숫자, 특수문자 포함 8자 이상</small>
                    </div>

                    <div className="form-group">
                        <label>
                            <i className="fas fa-lock"></i>
                            비밀번호 확인
                        </label>
                        <input
                            type="password"
                            value={formData.confirmPassword}
                            onChange={(e) => setFormData({...formData, confirmPassword: e.target.value})}
                            placeholder="비밀번호를 다시 입력하세요"
                            required
                        />
                    </div>

                    <button type="submit" className="register-button">
                        <i className="fas fa-user-plus"></i>
                        회원가입
                    </button>

                    <div className="security-features">
                        <div className="feature">
                            <i className="fas fa-lock"></i>
                            <span>암호화 저장</span>
                        </div>
                        <div className="feature">
                            <i className="fas fa-shield-alt"></i>
                            <span>보안 인증</span>
                        </div>
                        <div className="feature">
                            <i className="fas fa-user-shield"></i>
                            <span>권한 관리</span>
                        </div>
                    </div>
                </form>

                <div className="register-footer">
                    <p>이미 계정이 있으신가요? <a href="/login">로그인</a></p>
                </div>
            </div>
        </div>
    );
}

export default Register;
