import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from '../utils/axios';
import '../styles/Register.css';

function Register() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    // 회원가입 프로세스 상태 관리
    const [registrationProcess, setRegistrationProcess] = useState({
        requestValidation: { status: 'pending', message: null },
        passwordEncoding: { status: 'pending', message: null },
        duplicateCheck: { status: 'pending', message: null },
        userCreation: { status: 'pending', message: null },
        dbSave: { status: 'pending', message: null }
    });

    const handleRegister = async (e) => {
        e.preventDefault();
        
        try {
            // 1. 요청 데이터 검증
            setRegistrationProcess(prev => ({
                ...prev,
                requestValidation: {
                    status: 'processing',
                    message: '입력된 데이터 유효성 검사 중...',
                    details: {
                        username: username,
                        passwordLength: password.length
                    },
                    securityConcept: `
                        스프링 시큐리티의 데이터 유효성 검증
                        - @Valid 어노테이션을 통한 입력값 검증
                        - Custom Validator를 통한 비즈니스 규칙 검증
                    `
                }
            }));
            await new Promise(resolve => setTimeout(resolve, 2000));
            
            setRegistrationProcess(prev => ({
                ...prev,
                requestValidation: {
                    status: 'success',
                    message: '데이터 검증 완료',
                    details: {
                        username: username,
                        passwordLength: password.length,
                        validation: {
                            usernameValid: true,
                            passwordValid: true
                        }
                    }
                }
            }));

            // 2. 비밀번호 인코딩 과정
            setRegistrationProcess(prev => ({
                ...prev,
                passwordEncoding: {
                    status: 'processing',
                    message: 'BCryptPasswordEncoder로 비밀번호 암호화 중...',
                    securityConcept: `
                        스프링 시큐리티 비밀번호 인코딩
                        - PasswordEncoder 인터페이스 사용
                        - BCryptPasswordEncoder: 해시 함수를 통한 단방향 암호화
                        - Salt를 자동으로 생성하여 레인보우 테이블 공격 방지
                    `
                }
            }));
            await new Promise(resolve => setTimeout(resolve, 2000));
            
            setRegistrationProcess(prev => ({
                ...prev,
                passwordEncoding: {
                    status: 'success',
                    message: '비밀번호 암호화 완료',
                    details: {
                        algorithm: 'BCrypt',
                        strength: 10,
                        resultLength: 60
                    }
                }
            }));

            // 3. 중복 사용자 확인
            setRegistrationProcess(prev => ({
                ...prev,
                duplicateCheck: {
                    status: 'processing',
                    message: '사용자 중복 확인 중...',
                    securityConcept: `
                        스프링 시큐리티 사용자 관리
                        - UserDetailsService 인터페이스 구현
                        - 사용자 정보의 무결성 보장
                        - 중복 계정 생성 방지
                    `
                }
            }));
            await new Promise(resolve => setTimeout(resolve, 2000));

            // API 호출
            const response = await axios.post('/api/auth/register', {
                username,
                password
            });

            // 4. 사용자 엔티티 생성
            setRegistrationProcess(prev => ({
                ...prev,
                duplicateCheck: { status: 'success', message: '사용자 중복 확인 완료' },
                userCreation: {
                    status: 'success',
                    message: 'MemberEntity 생성 완료',
                    details: {
                        entityType: 'MemberEntity',
                        fields: ['id', 'username', 'password', 'createdAt']
                    },
                    securityConcept: `
                        스프링 시큐리티 사용자 엔티티
                        - UserDetails 인터페이스 구현
                        - 권한 정보 관리 (GrantedAuthority)
                        - 계정 상태 관리 (잠금, 만료, 활성화)
                    `
                }
            }));
            await new Promise(resolve => setTimeout(resolve, 2000));

            // 5. DB 저장
            setRegistrationProcess(prev => ({
                ...prev,
                dbSave: {
                    status: 'success',
                    message: '데이터베이스 저장 완료',
                    details: {
                        table: 'members',
                        operation: 'INSERT'
                    },
                    securityConcept: `
                        스프링 시큐리티 영속성
                        - JPA와의 통합
                        - 트랜잭션 보안
                        - 데이터 접근 권한 관리
                    `
                }
            }));

            // 모든 과정이 완료된 후 잠시 대기
            await new Promise(resolve => setTimeout(resolve, 2000));

            alert('회원가입이 성공적으로 완료되었습니다!');
            navigate('/login');
        } catch (error) {
            // 에러 발생 시 해당 단계 실패 표시
            const errorStep = error.response?.data?.includes('exists') ? 'duplicateCheck' : 'dbSave';
            setRegistrationProcess(prev => ({
                ...prev,
                [errorStep]: {
                    status: 'error',
                    message: error.response?.data || '알 수 없는 오류가 발생했습니다.'
                }
            }));
            alert('회원가입 실패: ' + (error.response?.data || '알 수 없는 오류가 발생했습니다.'));
        }
    };

    const ProcessStep = ({ title, status, message, details, securityConcept }) => (
        <div className={`process-step ${status}`}>
            <div className="step-header">
                <h4>{title}</h4>
                <span className={`status-indicator ${status}`}>
                    {status === 'processing' ? '진행 중' :
                     status === 'success' ? '완료' :
                     status === 'error' ? '실패' : '대기 중'}
                </span>
            </div>
            <div className="step-content">
                <p>{message}</p>
                {details && status === 'success' && (
                    <pre className="step-details">
                        {JSON.stringify(details, null, 2)}
                    </pre>
                )}
                {securityConcept && (status === 'processing' || status === 'success') && (
                    <div className="security-concept">
                        <h5>스프링 시큐리티 개념</h5>
                        <pre>{securityConcept}</pre>
                    </div>
                )}
            </div>
        </div>
    );

    return (
        <div className="register-container">
            <div className="register-form-section">
                <h2>회원가입</h2>
                <form onSubmit={handleRegister}>
                    <div className="form-group">
                        <input
                            type="text"
                            placeholder="사용자명"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                        />
                    </div>
                    <div className="form-group">
                        <input
                            type="password"
                            placeholder="비밀번호"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </div>
                    <button type="submit">회원가입</button>
                </form>
            </div>

            <div className="registration-process-section">
                <h3>회원가입 진행 과정</h3>
                <div className="process-steps">
                    <ProcessStep
                        title="1. 입력 데이터 검증"
                        status={registrationProcess.requestValidation.status}
                        message={registrationProcess.requestValidation.message}
                        details={registrationProcess.requestValidation.details}
                        securityConcept={registrationProcess.requestValidation.securityConcept}
                    />
                    <ProcessStep
                        title="2. 비밀번호 암호화"
                        status={registrationProcess.passwordEncoding.status}
                        message={registrationProcess.passwordEncoding.message}
                        details={registrationProcess.passwordEncoding.details}
                        securityConcept={registrationProcess.passwordEncoding.securityConcept}
                    />
                    <ProcessStep
                        title="3. 사용자 중복 확인"
                        status={registrationProcess.duplicateCheck.status}
                        message={registrationProcess.duplicateCheck.message}
                        details={registrationProcess.duplicateCheck.details}
                        securityConcept={registrationProcess.duplicateCheck.securityConcept}
                    />
                    <ProcessStep
                        title="4. 사용자 엔티티 생성"
                        status={registrationProcess.userCreation.status}
                        message={registrationProcess.userCreation.message}
                        details={registrationProcess.userCreation.details}
                        securityConcept={registrationProcess.userCreation.securityConcept}
                    />
                    <ProcessStep
                        title="5. 데이터베이스 저장"
                        status={registrationProcess.dbSave.status}
                        message={registrationProcess.dbSave.message}
                        details={registrationProcess.dbSave.details}
                        securityConcept={registrationProcess.dbSave.securityConcept}
                    />
                </div>
            </div>
        </div>
    );
}

export default Register;
